/*b) No seu main(), crie um array de Strings chamado nomes. Após, peça pro usuário quantos nomes este deseja digitar e instancie seu array de nomes com este tamanho. Após, solicite para o usuário digitar esta quantia de nomes. Agora, crie uma função chamada verificaNomesRepetidos() que recebe esse array como parâmetro e imprime os nomes repetidos no array.*/import java.util.Scanner;public class Trabalho08{        public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        System.out.print("Quantos nomes você deseja digitar? ");        int tamanho = scan.nextInt();        scan.nextLine();        String[] nomes = new String[tamanho];        for (int i = 0; i < tamanho; i++) {            System.out.print("Digite o nome " + (i + 1) + ": ");            nomes[i] = scan.nextLine();        }        verificaNomesRepetidos(nomes);    }    public static void verificaNomesRepetidos(String[] nomes) {        System.out.println("Nomes repetidos:");        boolean nomeRepetido = false;        for (int i = 0; i < nomes.length; i++) {            for (int j = i + 1; j < nomes.length; j++) {                if (nomes[i].equals(nomes[j])) {                    System.out.println(nomes[i]);                    nomeRepetido = true;                    break;                }            }        }        if (!nomeRepetido) {            System.out.println("Nenhum nome repetido encontrado.");        }            }}